\" style=\"color:#000000;\">append</span><span class=\"pun\" style=\"color:#666600;\">(</span><span class=\"str\" style=\"color:#008800;\">\"Terry\"</span><span class=\"pun\" style=\"color:#666600;\">)</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"com\" style=\"color:#880000;\"># Terry arrives</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"pun\" style=\"color:#666600;\">&gt;&gt;&gt;</span><span class=\"pln\" style=\"color:#000000;\"> queue</span><span class=\"pun\" style=\"color:#666600;\">.</span><span class=\"pln\" style=\"color:#000000;\">append</span><span class=\"pun\" style=\"color:#666600;\">(</span><span class=\"str\" style=\"color:#008800;\">\"Graham\"</span><span class=\"pun\" style=\"color:#666600;\">)</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"com\" style=\"color:#880000;\"># Graham arrives</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"pun\" style=\"color:#666600;\">&gt;&gt;&gt;</span><span class=\"pln\" style=\"color:#000000;\"> queue</span><span class=\"pun\" style=\"color:#666600;\">.</span><span class=\"pln\" style=\"color:#000000;\">popleft</span><span class=\"pun\" style=\"color:#666600;\">()</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"com\" style=\"color:#880000;\"># The first to arrive now leaves</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"str\" style=\"color:#008800;\">\'Eric\'</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"pun\" style=\"color:#666600;\">&gt;&gt;&gt;</span><span class=\"pln\" style=\"color:#000000;\"> queue</span><span class=\"pun\" style=\"color:#666600;\">.</span><span class=\"pln\" style=\"color:#000000;\">popleft</span><span class=\"pun\" style=\"color:#666600;\">()</span><span class=\"pln\" style=\"color:#000000;\"> </span><span class=\"com\" style=\"color:#880000;\"># The second to arrive now leaves</span><span class=\"pln\" style=\